import { useEffect, useState } from 'react';
import GameRow from './GameRow'
import { WORDLIST } from '../wordlist/cuvinte';


function GameGrid() {
    // fetch from a database
    const startDate = new Date().valueOf();
    const now = Date.now();
    const index = Math.floor((now - startDate) / 86400000);
    const secretWord = WORDLIST[index % WORDLIST.length];

    const [rowIdx, setRowIdx] = useState(0);
    const [words, updateWords] = useState(Array(6).fill(''));
    const [revealStates, setRevealStates] = useState(Array(6).fill(false));

    const updateWordAtRow = (word) => {
        const updatedWords = [...words];
        updatedWords[rowIdx] = word;
        updateWords(updatedWords);
    }

    useEffect(() => {
        const handleKeyDown = (event) => {
          if (event.key === 'Enter') {
            if (words[rowIdx].length !== 5 || !WORDLIST.includes(words[rowIdx])) {
                return;
            }

            setRevealStates((prevStates) => {
                const newStates = [...prevStates];
                newStates[rowIdx] = true;
                return newStates;
            });

            setRowIdx(Math.min(rowIdx + 1, 5));
          } else if (event.key == 'Backspace') {
            let modifiedWords = words.map((val, idx) => {
                if (idx == rowIdx) {
                    val = val.substring(0, val.length-1);
                }

                return val;
            });

            updateWords(modifiedWords);
          } else if (event.key.match(/[a-z]/i) && event.key.length == 1 && words[rowIdx].length < 5) {
            let modifiedWords = words.map((val, idx) => {
                if (idx == rowIdx) {
                    val = val + event.key;
                }

                return val;
            });

            updateWords(modifiedWords);
          }
        };

        window.addEventListener('keydown', handleKeyDown);

        return () => {
          window.removeEventListener('keydown', handleKeyDown);
        };
      }, [words, rowIdx]);


    return (
        <div>
            <GameRow word={words[0]} secretWord={secretWord} reveal={revealStates[0]} />
            <GameRow word={words[1]} secretWord={secretWord} reveal={revealStates[1]} />
            <GameRow word={words[2]} secretWord={secretWord} reveal={revealStates[2]} />
            <GameRow word={words[3]} secretWord={secretWord} reveal={revealStates[3]} />
            <GameRow word={words[4]} secretWord={secretWord} reveal={revealStates[4]} />
            <GameRow word={words[5]} secretWord={secretWord} reveal={revealStates[5]} />
        </div>
    )
}

export default GameGrid;
