import { useState } from 'react';
import Letter from './Letter'

function GameRow() {
    const [word, setWord] = useState(Array());
    return (
        <div className='game-row'>
            {word.forEach((char) => {
                <Letter char={char} state={1} />
            })}
            <button onClick={setWord("hello")}></button>
        </div>
    )
}

export default GameRow;
