import { useEffect, useState } from "react";

function Letter(props) {
    const [flipped, setFlipped] = useState(false);
    const [colored, setColored] = useState(false);

    useEffect(() => {
        const delayBeforeReveal = 500 * (props.pos + 1);
        const flipDuration = 600;

        const flipTimeout = setTimeout(() => {
            setFlipped(true);
        }, delayBeforeReveal);

        const colorTimeout = setTimeout(() => {
            setColored(true);
        }, delayBeforeReveal + flipDuration);

        return () => {
            clearTimeout(flipTimeout);
            clearTimeout(colorTimeout);
        };
    }, [props.pos]);

    const letterStateClass = colored ? letter_classname(props.state) : "empty-letter";

    return (
        <div
            className={`letter-box ${flipped ? "letter-flip" : ""} ${letterStateClass}`}>
            <p className="letter">{props.char}</p>
        </div>
    );
}

function letter_classname(state) {
    if (state === 0) {
        return "empty-letter";
    } 
    if (state === 1) {
        return "half-letter";
    }
    return "guessed-letter";
}

export default Letter;
