import Letter from './Letter';

function getCorrespondingState(word, secret) {
    word = word.toUpperCase();
    secret = secret.toUpperCase();

    let states = Array(5).fill(0);
    Array.from(word).map((ch, idx) => {
        if (ch == secret[idx]) {
            states[idx] = 2;
            let modifiedString = secret.substring(0, idx) + "|" + secret.substring(idx+1);
            secret = modifiedString;
        }
    });

    Array.from(word).forEach((ch, idx) => {
        if (secret.includes(ch) && states[idx] == 0) {
            states[idx] = 1;
            let modifiedString = secret.substring(0, idx) + secret.substring(idx+1);
            secret = modifiedString;
        }
    });

    return states;
}

function GameRow(props) {
    let word = props.word;
    let secret = props.secretWord;
    let states = [];
    if (props.reveal) {
        states = getCorrespondingState(word, secret);
    }

    return (
        <div className='game-row'>
            {Array.from(props.word).map((char, index) => (
                <Letter key={index} char={char.toUpperCase()} state={props.reveal ? states[index] : 0} />
            ))}
            {(5-props.word.length) > 0 && Array(5-props.word.length).fill(0).map((_, idx) => (
                    <Letter key={idx} char={''} state={0} />
            ))}
        </div>
    );
}

export default GameRow;
