import { useEffect, useState } from "react";

function Letter(props) {
    const [flipped, setFlipped] = useState(false);
    useEffect(() => {
        setTimeout(() => {
            setFlipped(true);
        }, 1500);
    }, [props.pos]);

    return (
        <div className={`letter-box ${flipped && letter_classname(props.state)} ${props.reveal && 'letter-flip'}`} 
        style={{transitionDelay: `${0.5 * props.pos}s`}}>
            <p className="letter">{props.char}</p>
        </div>
    )
}

function letter_classname(state) {
    if (state === 0) {
        return "empty-letter";
    } 
    if (state === 1) {
        return "half-letter";
    }

    return "guessed-letter";
}

export default Letter;
