import { useEffect, useState } from 'react';
import GameRow from './GameRow'
import { WORDLIST } from '../wordlist/cuvinte';


function GameGrid() {
    // fetch from a database
    const epochMs = new Date(2000, 0).valueOf()
    const now = Date.now()
    const msInDay = 86400000
    const index = Math.floor((now - epochMs) / msInDay)
    const nextday = (index + 1) * msInDay + epochMs


    const [rowIdx, setRowIdx] = useState(0);
    const [words, updateWords] = useState(Array(6).fill(""));

    useEffect(() => {
        const handleKeyDown = (event) => {
            console.log(event.key);
          if (event.key === 'Enter') {
            // check if the word is valid

            // update colors

            let idx = rowIdx+1;
            if (idx >5) idx = 5;
            setRowIdx(idx);
          } else if (event.key == 'Backspace') {
            let modifiedWords = words.map((val, idx) => {
                if (idx == rowIdx) {
                    val = val.substring(0, val.length-1);
                }

                return val;
            });

            updateWords(modifiedWords);
          } else if (event.key.match(/[a-z]/i) && event.key.length == 1) {
            let modifiedWords = words.map((val, idx) => {
                if (idx == rowIdx) {
                    val = val + event.key;
                }

                return val;
            });

            updateWords(modifiedWords);
          }
        };

        window.addEventListener('keydown', handleKeyDown);

        return () => {
          window.removeEventListener('keydown', handleKeyDown);
        };
      }, [words, rowIdx]);



    return (
        <div>
            <GameRow word={words[0]} />
            <GameRow word={words[1]} />
            <GameRow word={words[2]} />
            <GameRow word={words[3]} />
            <GameRow word={words[4]} />
            <GameRow word={words[5]} />
        </div>
    )
}

export default GameGrid;
