function Letter(props) {
    return (
        <div className={"letter-box " + letter_classname(props.state)}>
            {props.state == 0 && <p>d</p>}
            {(props.state == 1 || props.state == 2) && <p className="letter">{props.char}</p>}
        </div>
    )
}

function letter_classname(state) {
    if (state == 0) {
        return "empty-letter";
    } 
    if (state == 1) {
        return "half-letter";
    }

    return "guessed-letter";
}

export default Letter;
